var Notes=JSON.parse(localStorage.getItem("Notes")),$Notes=$("#Notes"),$AddButton=$("#AddButton"),$NoteText=$("#NoteText");function UpdateNotes(){localStorage.setItem("Notes",JSON.stringify(Notes))}function CreateNote(a){$Notes.append('<div class="Note"><div class="Date">'+a.date+'<span class="RemoveButton">Remove</span></div><div class="Text">'+a.text+"</div></div>")}if(Notes===null){Notes={Data:[]};UpdateNotes()}else{$.each(Notes.Data,function(){CreateNote(this)})}$AddButton.on("click",function(){var b=new Date(),a={date:b.toDateString()+" "+b.toLocaleTimeString(),text:$NoteText.val()};Notes.Data.push(a);UpdateNotes();CreateNote(a);$NoteText.val("")});$(document).on("click",".RemoveButton",function(){var a=$(this).parent().parent();if(a.index()>=0){Notes.Data.splice(a.index(),1);UpdateNotes();a.remove()}});